import React, { Component } from "react";
import './RecipeApp.css'

class List2App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [
          { 
            id: 0,
            ingredient: 'Eggs',
            quantity: 1,
            section: 'Refrigerated'
          },
      ],
      newItem: "",
      newQuantity: 1,
      newSection: ''
    };
  }


  componentDidMount() {
    // for all items in state
    for (let key in this.state) {
        // if the key exists in localStorage
        if (localStorage.hasOwnProperty(key)) {
          // get the key's value from localStorage
          let value = localStorage.getItem(key);

          // parse the localStorage string and setState
          try {
            value = JSON.parse(value);
            this.setState({ [key]: value });

          } catch (e) {
            // handle empty string
            this.setState({ [key]: value });
          }
        }
      }

    // add event listener to save state to localStorage
    // when user leaves/refreshes the page
    window.addEventListener(
      "beforeunload",
      this.saveStateToLocalStorage.bind(this)
    );
  }

  componentWillUnmount() {
    window.removeEventListener(
      "beforeunload",
      this.saveStateToLocalStorage.bind(this)
    );

    // saves if component has a chance to unmount
    this.saveStateToLocalStorage();
  }


  saveStateToLocalStorage() {
    // for every item in React state
    for (let key in this.state) {
      // save to localStorage
      localStorage.setItem(key, JSON.stringify(this.state[key]));
    }
  }

  updateInput(key, value) {
    // update react state
    this.setState({ [key]: value });

  }


  addItem() {
    // create a new item
    console.log(" newSection?:"+ this.state.newQuantity)
    const newItem = {
      id: 1 + Math.random(),
      value: this.state.newItem.slice(),
      quantity: this.state.newQuantity.slice(),
      section: this.state.newSection.slice()
    };
    console.log(" mount value:"+ this.state.newItem)
    console.log(" mount value:"+ this.state.newSection)


    // copy current list of items
    const list = [...this.state.list];
    console.log(this.state.list.length)
    //console.log("list:"+this.state.list[0].section)
    // add the new item to the list
    list.push(newItem);

    // update state with new list, reset the new item input
    this.setState({
      list,
      newItem: "",
      newQuantity: "1",
      newSection: "Other"
    });

  }

  deleteItem(id) {
    // copy current list of items
    const list = [...this.state.list];
    // filter out the item being deleted
    const updatedList = list.filter(item => item.id !== id);

    this.setState({ list: updatedList });

  }


  render() {
    return (
      <div className="App">

          <hr />

        <div className="container">
          <h3>Add Groceries Here</h3>
          <br />
          <input
            type = "number"
            min = "1"
            class = "num-input"
            placeholder = "#"
            value={this.state.newQuantity}
            //onChange={e=>this.updateInput("newQuantity", e.target.value)}
          />
          <input
            type="text"
            placeholder="enter items here"
            value={this.state.newItem}
            //onChange={e => this.updateInput("newItem", e.target.value)}
          />




          <div class="checkbox-area">
            <label class="container-radio">Produce
                <input type="radio" id ="add" value="Produce" name="foodtype" />
                <span class="checkmark-radio"></span>
            </label>
            <label class="container-radio">Refrigerated
            <input type="radio" id ="add" value ="Refrigerated" name="foodtype" />
            <span class="checkmark-radio"></span>
            </label>
            <label class="container-radio">Frozen
            <input type="radio"id="add" value="Frozen" name="foodtype" />
            <span class="checkmark-radio"></span>
            </label>
            <label class="container-radio">Pharmaceuticals
            <input type="radio"id="add" value="Pharma" name="foodtype" />
            <span class="checkmark-radio"></span>
            </label>

            <label class="container-radio">Other Food (food at room temp)
            <input type="radio" id ="add" name="foodtype" value="Other" />
            <span class="checkmark-radio"></span>
            </label>
            <label class="container-radio">Other items (not food)
                <input type="radio" id ="add" name="foodtype" value="Nonfood" />
                <span class="checkmark-radio"></span>
            </label>
          </div>


          <button
            onClick={() => this.addItem()}
            //disabled={!this.state.newItem.length}
          >
            &#43; Add
          </button>
          <br /> <br />
          <ul>
            {this.state.list.map(item => {
              return (
                <li key={item.id} className="list-item">
                <div className="list-item__container">
                  <div>
                   <span className="quantity-diplay">{item.quantity}</span> {item.value}
                  </div>
                  <div>   
                    <span className="section-dislay">{item.section}</span>
                    <button 
                      className = "button x-button"   
                      onClick={() => this.deleteItem(item.id)}>
                      X
                    </button>
                  </div>
                </div>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    );
  }
}

export default List2App;


