import React, { Component } from 'react'
import Select from 'react-select';



export default class GrocInput extends Component {
    static defaultProps = {
      onClose() {},
      onSave() {}
    }



    state = {
      item: "",
      selectedOption: null
    };

    handleChange = (e) => {
      this.setState({[e.target.name]: e.target.value});
    }

    state = {
      selectedOption: null,
    }
    // handleChange2 = (selectedOption) => {
    //   this.setState({ selectedOption });
    //   console.log(`Option selected:`, selectedOption);
    // }

    handleChangeSec = (selectedOption, e) => {
        //this.setState({[e.target.name]: e.target.value});
        this.setState({selectedOption: selectedOption});
        console.log(`Option picked: `, selectedOption.value)

      }

    getOptions =() => {
      console.log(this.props.sections)
      return [

        { value: 'Produce', label: "Produce" },
        { value: 'Frozen', label: "Frozen"},
        { value: 'Refrigerated', label: "Refrgerated" },
        { value: 'Center Isle Food', label: 'Center Isle Food'},
        { value: 'Other Food', label: 'Other Food'},
        { value: 'Front of store', label: 'Front of store'},
        { value: 'Household', label: 'Household'},
        { value: 'Drugs', label: 'Drugs'},
        { value: 'Bath/Hygiene', label: 'Bath/Hygiene'},
        { value: 'Other/Not Food', label: 'Other/Not Food'}
      ]
    }

    handleSubmit = (e) => {
      e.preventDefault();

      this.props.onSave({...this.state});
      this.setState({
        item: '',
        quantity: 1,
        section: ''
      })
      this.props.changeSection("All")
    }


    render() {

      const {item, quantity, selectedOption} = this.state;
      return (
        <div className="recipe-form-container">
          <form className='recipe-form' onSubmit={this.handleSubmit}>

            <div className='recipe-form-line'>
              <label htmlFor='recipe-item-input'>item:</label>
              <br />
              <input
                id='recipe-item-input'
                key='item'
                name='item'
                type='text'
                value={item}
                autoComplete="on"
                onChange={this.handleChange}
                required
                />
                <label htmlFor='recipe-quantity-input'>quantity:</label>
                <br />
                <input
                  id='recipe-quantity-input'
                  key='quantity'
                  name='quantity'
                  type='number'
                  min="1"
                  value={quantity || 1}
                  autoComplete="off"
                  onChange={this.handleChange}
                  required
                  />
              </div>
              <div className='recipe-form-line'>
                <label htmlFor='recipe-item-input'> section:</label>

                <Select 

                  id='recipe-section-input'
                  key='section'
                  name='section'
                  autoComplete="on"
                  value={selectedOption}
                  onChange={this.handleChangeSec}
                  options={ this.getOptions() }

                />



                <select id="recipe-section-input" 
                  key='section1'
                  name='section1'
                  onChange={this.handleChangeSec}>
                  {this.props.sections.map((optionItem)=>{
                    return <option value={optionItem.value}>{optionItem.value}</option>
                  })}

              </select>
            </div>


            <button
              type="submit"
              className="buttons btn btn-primary btn-block"
              style={{alignSelf: 'flex-end', marginRight: 0}}
            >
              add item
            </button>



          </form>
        </div>
      )
    }
  }
