constructor(props){
  super(props)
  this.handleAddOne = this.handleAddOne.bind(this)
  this.handleMinusOne = this.handleMinusOne.bind(this)
  this.state = { count: 0,   
                  name: "Vick", 
                  age: 30,
                  ninjas: [
                    {name: 'Ryu', belt: 'black'},
                    {name: 'Yoshi', belt: 'green'}
                  ]

                }

}

handleAddOne(){
  this.setState((prevState)=>{
    return {
      count: prevState.count +1
    }
  })
}

handleMinusOne(){
  console.log("Sub");
  this.setState((prev)=>{
    return{
      count: prev.count-1
    }

  })
}

handleReset(){
