import React, { Component } from 'react'
import Select from 'react-select';


export default class GrocInput extends Component {
    static defaultProps = {
      onClose() {},
      onSave() {}
    }

    state = {
      title: ""
    };

    handleChange = (e) => {
      this.setState({[e.target.name]: e.target.value});
    }

    handleChangeSec = (selectedOption) => {
        //this.setState({[e.target.name]: e.target.value});
        this.setState({selectedOption: selectedOption.label});
        console.log(`Option picked: `, selectedOption.value)

      }

    getOptions =() => {
      return [
        { value: 'produce', label: 'Produce' },
        { value: 'frozen', label: 'Frozen' },
        { value: 'refrigerated', label: 'Refrigerated' },
        { value: 'otherFood', label: "Other Food"},
        { value: 'drugs', label: "Drugs"},
        { value: 'notFood', label: "Other/Not Food"}

      ]
    }

    handleSubmit = (e) => {
      e.preventDefault();
      this.props.onSave({...this.state});
      this.setState({
        title: '',
        section: ''
      })
    }


    handleChangeFilt = (e) => {
      this.props.onChange(e.target.value);
    }

    render() {
      const {title, selectedOption} = this.state;

      return (
        <div className="recipe-form-container">
          <form className='recipe-form' onSubmit={this.handleSubmit}>

            <div className='recipe-form-line'>
              <label htmlFor='recipe-title-input'>item:</label>
              <br />
              <input
                id='recipe-title-input'
                key='title'
                name='title'
                type='text'
                value={title}
                autoComplete="off"
                onChange={this.handleChange}/>
              </div>
              <div className='recipe-form-line'>
                <label htmlFor='recipe-title-input'> section:</label>

                <Select 
                  id='recipe-section-input'
                  key='section'
                  name='section'
                  autoComplete="on"
                  //value={selectedOption}
                  onChange={this.handleChangeSec}
                  options={ this.getOptions() }
                />
            </div>


            <button
              type="submit"
              className="buttons"
              style={{alignSelf: 'flex-end', marginRight: 0}}
            >
              add item
            </button>

            <br />
            Filter by: 

            <h5>You choose  {this.props.filterName}!</h5>
            <select id="great-names" 
            onChange={e=>this.props.onChangeFilt(e.target.value)}>
            <option value="All">All</option>
            <option value="Produce">Produce</option>
            <option value="Refrigerated">Refrigerated</option>
            <option value="Frozen">Frozen</option>
          </select>


          </form>
        </div>
      )
    }
  }
