import React, {Component} from 'react';
import ReactDOM from 'react-dom';
//import 'bootstrap/dist/css/bootstrap.css'
import './styles/index.css';
//import App from './App';
import {BrowserRouter, Route, Switch} from 'react-router-dom';
import Navbar from "./Navbar"
import RecipeApp from "./recipes/RecipeApp";
import uuid from 'uuid';
import List from "./groceries/GrocApp";
import About from "./About";
import * as serviceWorker from './serviceWorker';



class CentralApp extends Component {
    constructor(props) {
      super(props);
      //this.onSave = this.onSave.bind(this);
      //this.onDelete = this.onDelete.bind(this);
      this.state = {
        grocs: [
          {
            id: 0,
            title: "Eggs",
            section: "Refrigerated"
          },
          {
            id: 1,
            title: "Bananas",
            section: "Produce"
          }
        ],
        nextGrocId: uuid(),
        filterChoice: 'All'     
      }
    }




 //LocalStorage:
 componentDidMount() {
    // for all items in state
    for (let key in this.state) {
        // if the key exists in localStorage
        if (localStorage.hasOwnProperty(key)) {
          // get the key's value from localStorage
          let value = localStorage.getItem(key);

          // parse the localStorage string and setState
          try {
            value = JSON.parse(value);
            this.setState({ [key]: value });
          } catch (e) {
            // handle empty string
            this.setState({ [key]: value });
          }
        }
      }

    // add event listener to save state to localStorage
    // when user leaves/refreshes the page
    window.addEventListener(
      "beforeunload",
      this.saveStateToLocalStorage.bind(this)
    );
  }

  componentWillUnmount() {
    window.removeEventListener(
      "beforeunload",
      this.saveStateToLocalStorage.bind(this)
    );

    // saves if component has a chance to unmount
    this.saveStateToLocalStorage();
  }


  saveStateToLocalStorage() {
    // for every item in React state
    for (let key in this.state) {
      // save to localStorage
      localStorage.setItem(key, JSON.stringify(this.state[key]));
    }
  }

  updateInput(key, value) {
    // update react state
    this.setState({ [key]: value });

  }


    onSave = (grocs) => {
      this.setState((prevState) => {
        const newGroc = {...grocs, id: this.state.nextGrocId};
        return {
          nextGrocId: prevState.nextGrocId + 1,
          grocs: [...this.state.grocs, newGroc],
          filterChoice: "All"
        }
      });
    }

    onDelete = (id) => {
      const grocs = this.state.grocs.filter(groc => groc.id !== id);
      this.setState({grocs});
    }


    handleChangeFilt = (e) => {
      console.log(e.target.value)
      //this.props.onChange(e.target.value);
    }

    changeSection = (newSection) => {
      //console.log("New Section: "+newSection)
      this.setState({
        filterChoice: newSection
      });
      //console.log("Filter Choice: "+this.state.filterChoice)
      // const grocs = this.state.grocs.filter(groc => groc.selectedOption === newSection);
      //   this.setState({grocs});

    }

    render(){
        const NotFoundPage = () =>(
            <div className="App">
                <div className="containter intro"> 
                    <h1>Welcome to Shopping Buddy. </h1>
                    <img src="shop.jpg" alt="Grocery Store" />
                    <p>An app for collecting and organizing your favorite recipes
                    and pushing them to a sortable shopping list.</p>
                </div>
            </div>
        )


        const routes = (
            <BrowserRouter basename={process.env.PUBLIC_URL}>
            <div>
             <Navbar />
             <hr />
             <Switch>
                <Route path="/about" component={About}  exact={true}/>
                <Route path="/recipes" component={RecipeApp} />
                <Route path="/groceries" component={List} />
                <Route path="/list" component={List} />
                <Route component={NotFoundPage} />
              </Switch>
              </div>
            </BrowserRouter>
        )

        return(
            <div>
                {routes}
                />
            </div>
        )
    }


}


ReactDOM.render(<CentralApp />, document.querySelector('#root'));


// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
